n: Left;padding-bottom: 20px;}.textContent a,.textContentLast a {color: #2C9AB7;text-decoration: underline;}.nestedContainer {background-color: #F2F2F2;border: 1px solid #CCCCCC;}.emailButton {background-color: #2C9AB7;border-collapse: separate;border-radius: 4px;}.buttonContent {color: #FFFFFF;font-family: "Lato", Helvetica;font-size: 18px;font-weight: 400;line-height: 100%;padding: 15px;text-align: center;}.buttonContent a {color: #FFFFFF;display: block;text-decoration: none;}.buttonContent.small {color: #FFFFFF;font-size: 13px;padding: 10px;}.emailCalendar {background-color: #FFFFFF;border: 1px solid #CCCCCC;}.emailCalendarMonth {background-color: #2C9AB7;color: #FFFFFF;font-family: Helvetica, Arial, sans-serif;font-size: 16px;font-weight: bold;padding-top: 10px;padding-bottom: 10px;text-align: center;}.emailCalendarDay {color: #2C9AB7;font-family: Helvetica, Arial, sans-serif;font-size: 60px;font-weight: bold;line-height: 100%;padding-top: 20px;padding-bottom: 20px;text-align: center;}/*////// MOBILE STYLES //////*/@media only screen and (max-width: 480px) {/*////// CLIENT-SPECIFIC STYLES //////*/body {width: 100% !important;min-width: 100% !important;}/* Force iOS Mail to render the email at full width. *//*////// FRAMEWORK STYLES //////*/table[id="emailBody"],table[class="flexibleContainer"] {width: 100% !important;}img[class="flexibleImage"] {height: auto !important;width: 100% !important;}/*Make buttons in the email span thefull width of their container, allowingfor left- or right-handed ease of use.*/table[class="emailButton"] {width: 100% !important;}td[class="buttonContent"] {padding: 0 !important;}td[class="buttonContent"] a {padding: 15px !important;}td[class="textContentLast"],td[class="imageContentLast"] {padding-top: 20px !important;}/*////// GENERAL STYLES //////*/td[id="bodyCell"] {padding-top: 10px !important;padding-Right: 10px !important;padding-Left: 10px !important;}}/*custom*/h5.right {float: right;font-weight: 300;font-size: 15px;}[]:hover,.